            x += 100;

            if (x === 800)
            {
                x = 100;
                y += 100;
            }
        }

        this.input.on('pointerdown', function () {

            //  Every sprite using the global 'square' animation will now pause,
            //  because we're pausing the Animation instance itself:

            if (square.paused)
            {
                square.resume();
            }
            else
            {
                square.pause();
            }

        });
