            {
                text.setText('Fade out global');
            },

            targets: this.sound,
            volume: 0,

            ease: 'Linear',
            duration: 2000,

            onComplete: fn
        });
    },

    function(fn)
    {
        this.tweens.add({

            onStart: function ()
            {
                text.setText('Fade in global');
            },

            targets: this.sound,
            volume: 1,

            ease: 'Linear',
            duration: 2000,

            onComplete: fn
        });
    },

    function(fn)
    {
        this.sound.once('pauseall', function (soundManager)
        {
            text.setText('Pause all');
            this.time.addEvent({
                delay: 1500,
                callback: fn,
                callbackScope: this
            });
        }, this);

        this.sound.pauseAll();
    },

    function(fn)
    {
        this.sound.once('resumeall', function (soundManager)
        {
            text.setText('Resume all');
            this.time.addEvent({
                delay: 2000,
                callback: fn,
                callbackScope: this
            });
        }, this);

        this.sound.resumeAll();
    },

    function(fn)
    {
        this.sound.once('stopall', function (soundManager)
        {
            text.setText('Stop all');
            this.time.addEvent({
                delay: 1500,
                callback: fn,
                callbackScope: this
            });
        }, this);

        this.sound.stopAll();
    },

    function(fn)
    {
        audioSprite.once('play', function (sound)
        {
            text.setText('Play sprite');
            this.time.addEvent({
                delay: 1500,
                callback: fn,
                callbackScope: this
            });
        }, this);

        audioSprite.play('07');
    },

    function(fn)
    {
        audioSprite.once('pause', function (sound)
        {
            text.setText('Pause sprite');
            this.time.addEvent({
                delay: 1000,
                callback: fn,
                callbackScope: this
            });
        }, this);

        audioSprite.pause();
    },

    function(fn)
    {
        audioSprite.once('resume', function (sound)
        {
            text.setText('Resume sprite');
            this.time.addEvent({
                delay: 1500,
                callback: fn,
                callbackScope: this
            });
        }, this);

        audioSprite.resume();
    },

    function(fn)
    {
        audioSprite.once('play', function (sound)
        {
            text.setText('Multiple sprites');
            this.time.addEvent({
                delay: 10000,
                callback: fn,
                callbackScope: this
            });
        }, this);

        var sounds = ['01', '02', '03', '03', '05'];

        for (var i=0; i<sounds.length; i++)
        {
            this.time.addEvent({
                delay: i * 2000,
                callback: audioSprite.play.bind(audioSprite, sounds[i]),
                callbackScope: audioSprite
            });
        }
    },

    function(fn)
    {
        audioSprite.once('play', function (sound)
        {
            text.setText('Loop sprite');
            this.time.addEvent({
                delay: 4000,
                callback: fn,
                callbackScope: this
            });
        }, this);

        audioSprite.play('06', {
            loop: true
        });
    },

    function(fn)
    {
        this.tweens.add({

            onStart: function ()
            {
                text.setText('Fade out sprite');
            },

            targets: audioSprite,
            volume: 0,

            ease: 'Linear',
            duration: 4000,

            onComplete: function()
            {
                audioSprite.volume = 1;
                audioSprite.stop();

                fn();
            }
        });
    }
];

function create ()
{
    this.sound.pauseOnBlur = false;

    var prometheus = this.add.image(400, 300, 'prometheus');
    prometheus.setScale(600/prometheus.height);

    text = this.add.text(400, 300, 'Loading...', {
        fontFamily: '\'Sorts Mill Goudy\', serif',
        fontSize: 80,
        color: '#fff',
        align: 'center'
    });
    text.setOrigin(0.5);
    text.setShadow(0, 1, "#888", 2);

    first = this.sound.add('overture', { loop: true });
    second = this.sound.add('overture', { loop: true });
    audioSprite = this.sound.addAudioSprite('creatures');

    enableInput.call(this);
}

function chain(i)
{
    return function()
    {
        if (tests[i])
        {
            tests[i].call(this, chain.call(this, ++i));
        }
        else
        {
            text.setText('Complete!');

            this.time.addEvent({
                delay: 5000,
                callback: enableInput,
                callbackScope: this
            });
        }
    }.bind(this);
}

function enableInput()
{
    text.setText('Click to start');

    this.input.once('pointerdown', function (pointer)
    {
        tests[0].call(this, chain.call(this, 1));
    }, this);
}

