        // this.cameras.main.setZoom(0.5);
        // this.cameras.main.setDeadzone(400, 200);

        this.cameras.main.startFollow(this.player, true);
        // this.cameras.main.startFollow(player, true, 0.1, 0.1);

        this.cameras.main.setDeadzone(400, 200);
        this.cameras.main.setZoom(0.5);

        this.input.on('pointerdown', function () {
            this.moveCam = (this.moveCam) ? false: true;
        }, this);

        if (this.cameras.main.deadzone)
        {
            const graphics = this.add.graphics().setScrollFactor(0);
            graphics.lineStyle(2, 0x00ff00, 1);
            graphics.strokeRect(200, 200, this.cameras.main.deadzone.width, this.cameras.main.deadzone.height);
        }

        this.text = this.add.text(32, 32).setScrollFactor(0).setFontSize(64).setColor('#ffffff');
    }

    update ()
    {
        const cam = this.cameras.main;

        if (cam.deadzone)
        {
            this.text.setText([
                'Cam Control: ' + this.moveCam,
                'ScrollX: ' + cam.scrollX,
                'ScrollY: ' + cam.scrollY,
                'MidX: ' + cam.midPoint.x,
                'MidY: ' + cam.midPoint.y,
                'deadzone left: ' + cam.deadzone.left,
                'deadzone right: ' + cam.deadzone.right,
                'deadzone top: ' + cam.deadzone.top,
                'deadzone bottom: ' + cam.deadzone.bottom
            ]);
        }
        else if (cam._tb)
        {
            this.text.setText([
                'Cam Control: ' + this.moveCam,
                'ScrollX: ' + cam.scrollX,
                'ScrollY: ' + cam.scrollY,
                'MidX: ' + cam.midPoint.x,
                'MidY: ' + cam.midPoint.y,
                'tb x: ' + cam._tb.x,
                'tb y: ' + cam._tb.y,
                'tb right: ' + cam._tb.right,
                'tb bottom: ' + cam._tb.bottom
            ]);
        }

        this.player.setVelocity(0);

        if (this.moveCam)
        {
            if (this.cursors.left.isDown)
            {
                cam.scrollX -= 4;
            }
            else if (this.cursors.right.isDown)
            {
                cam.scrollX += 4;
            }

            if (this.cursors.up.isDown)
            {
                cam.scrollY -= 4;
            }
            else if (this.cursors.down.isDown)
            {
                cam.scrollY += 4;
            }
        }
        else
        {
            if (this.cursors.left.isDown)
            {
                this.player.setVelocityX(-800);
            }
            else if (this.cursors.right.isDown)
            {
                this.player.setVelocityX(800);
            }

            if (this.cursors.up.isDown)
            {
                this.player.setVelocityY(-800);
            }
            else if (this.cursors.down.isDown)
            {
                this.player.setVelocityY(800);
            }
        }
    }
}

