    c3.add(cam3, 'scrollX').listen();
    c3.add(cam3, 'scrollY').listen();
    c3.add(cam3, 'rotation').min(0).step(0.01).listen();
    c3.add(cam3, 'zoom', 0.1, 2).step(0.1).listen();
