        x: 100,
        ease: 'Quad.easeInOut',
        duration: 4000,
        repeat: -1,
        yoyo: true
    });

    this.tweens.add({
        targets: sprite3,
        y: 100,
        ease: 'Quad.easeInOut',
        duration: 4000,
        repeat: -1,
        yoyo: true
    });

