        x: -100,
        ease: 'Quad.easeInOut',
        duration: 4000,
        repeat: -1,
        yoyo: true
    });

    this.tweens.add({
        targets: containers,
        angle: { value: 360, duration: 6000 },
        scaleX: { value: 6, duration: 3000, yoyo: true, ease: 'Quart.easeInOut' },
        scaleY: { value: 0.1, duration: 3000, yoyo: true, ease: 'Quad.easeInOut' },
        repeat: -1,
        delay: function (target, key, value, index, total, tween) {
            return index * 16;
        }
    });
}

