function update() {

    if (add)
    {
        for (var i = 0; i < 250; ++i)
        {
            launch();

            if (blitter.children.length === 2000)
            {
                //  Create a new blitter object, as they can only hold 10k bobs each
                blitter = this.add.blitter(0, 0, 'starGraphics');
            }
        }

        updateDigits();
