    for (var index = 0, length = bobs.length; index < length; ++index)
    {
        var bob = bobs[index];
        bob.data.vy += gravity;

        bob.y += bob.data.vy;
        bob.x += bob.data.vx;

        if (bob.x > 1024)
        {
            bob.x = 1024;
            bob.data.vx *= -bob.data.bounce;
        }
        else if (bob.x < 0)
        {
            bob.x = 0;
            bob.data.vx *= -bob.data.bounce;
        }

        if (bob.y > 650)
        {
            bob.y = 650;
            bob.data.vy *= -bob.data.bounce;
        }
    }
}

