var WireframeScene = {};

WireframeScene.Start = function ()
{
    this.graphics;

    this.meshes = [];

    this.current;

    this.logo;
    this.sphere;
    this.torus;
    this.spike;

    this.computer;
    this.camera;

    this.sequence1 = false;
    this.sequence2 = false;

    this.modelData = {};
};

