        spotlight.x = pointer.worldX;
        spotlight.y = pointer.worldY;

    });

    text = this.add.text(10, 10, '').setDepth(1).setScrollFactor(0, 0);

    cursors = this.input.keyboard.createCursorKeys();

    var i;
