    var lowWind = {
        tween: this.tweens.addCounter({
            from: -5,
            to: 5,
            duration: 11 * SECOND,
            loop: Phaser.FOREVER,
            ease: 'Sine.easeInOut'
        }),
        processor: {
            active: true,
            update: function (particle)
            {
                particle.velocityX += lowWind.tween.getValue();
            }
        }
    };

    var highWind = {
        tween: this.tweens.addCounter({
            from: -10,
            to: 10,
            duration: 4 * SECOND,
