    rnd = Math.random;
}

function update()
{
    for (i = 0; i < 20; i++)
    {
        draw();
    }

    t = this.time.now / 100000;

    this.cameras.main.shake(500, t / 100);
    this.cameras.main.setZoom(1 + t);
}

function draw()
{
    var alpha = rnd();
    var tint = (0x00ffff * (rnd() * 0.1 + 0.8));
    var frame = ~~(rnd() * 22);

    rt.drawFrame("matrix", frame, rnd() * 800, rnd() * 600, alpha, tint);
}

