var tween;

function preload ()
{
    this.load.image('bubble', 'assets/particles/bubble.png');
}

function create ()
{
    rt = this.add.renderTexture(0, 0, 800, 600);

    trail = this.add.image(400, 300, 'bubble').setVisible(false);

    player = this.add.image(400, 300, 'bubble');

    tween = this.tweens.add({
        targets: trail,
        x: player.x,
        y: player.y,
        ease: 'Sine.easeInOut',
        duration: 50000,
        repeat: -1
    });
