    // var y = screenY / TILE_HEIGHT - screenX / (2 * TILE_WIDTH);

    var x = ((screenY * 2 / TILE_HEIGHT) + (screenX / TILE_WIDTH))/2;
    var y = (screenY * 2 / TILE_HEIGHT) - x;

    return { x: x, y: y }
}

