function create ()
{
    // Basic text wrapping based on width.
    this.make.text({
        x: 400,
        y: 100,
        text: 'The sky above the port was the color of television, tuned to a dead channel.',
        origin: { x: 0.5, y: 0.5 },
        style: {
            font: 'bold 25px Arial',
            fill: 'white',
            wordWrap: { width: 300 }
        }
    });

    // Basic wrap will NOT touch the whitespace in your text.
    this.make.text({
        x: 400,
        y: 250,
        text: '        Basic wrapping:        look        at        all        this        weird         space        ',
        origin: { x: 0.5, y: 0.5 },
        style: {
            font: 'bold 25px Arial',
            fill: 'white',
            wordWrap: { width: 300 }
        }
    });

    // Advanced wrap will collapse neighboring spaces into a single space and trim whitespace from
    // the start and end of each line.
    this.make.text({
        x: 400,
        y: 375,
        text: '        Advanced wrapping:        space        collapses        and        is        trimmed        ',
        origin: { x: 0.5, y: 0.5 },
        style: {
            font: 'bold 25px Arial',
            fill: 'white',
            wordWrap: { width: 300, useAdvancedWrap: true }
        }
    });

    // The advanced word wrapping algorithm will also break words that are longer than the specified
    // wrap width
    this.make.text({
        x: 400,
        y: 500,
        text: 'Long word incoming: Supercalifragilisticexpialidocious!',
        origin: { x: 0.5, y: 0.5 },
        style: {
            font: 'bold 25px Arial',
            fill: 'white',
            wordWrap: { width: 300, useAdvancedWrap: true }
        }
    });
}

var config = {
    type: Phaser.CANVAS,
    width: 800,
    height: 600,
    backgroundColor: '#2d2d2d',
    parent: 'phaser-example',
    scene: {
        preload: preload,
        create: create,
        update: update
    }
};

var images = [];
var game = new Phaser.Game(config);
var iter = 0;

function preload ()
{
    this.load.atlas('atlas', 'assets/atlas/megaset-2.png', 'assets/atlas/megaset-2.json');
}

function create ()
{
    var frames = ['atari400', 'bunny', 'cokecan', 'copy-that-floppy', 'hotdog'];
    for (var i = 0; i < frames.length; ++i) 
    {
        images[i] = this.add.tileSprite(i * 160, 0, 160, 600, 'atlas', frames[i]);
        images[i].originX = 0;
        images[i].originY = 0;
    }
}

function update ()
{
    var x = 1;
    for (var i = 0; i < images.length; ++i) 
    {
        images[i].tilePositionX += x;
        x *= -1;
    }
    iter += 0.01;
}

