geom/point/cross.js:
var config = {
    width: 800,
    height: 600,
    type: Phaser.AUTO,
    parent: 'phaser-example',
    scene: {
        create: create,
        update: update
    }
};

var game = new Phaser.Game(config);
var graphics;
var point;
var point2;
var text;

var angle = 0;

function create ()
{
    graphics = this.add.graphics({ lineStyle: { width: 2, color: 0x2266aa } });

    point = new Phaser.Math.Vector2(250, 0);
    point2 = new Phaser.Math.Vector2(250, 0);

    text = this.add.text(30, 30, '');

    this.input.on('pointermove', function (pointer) {
        point2.copy(pointer);

        point2.x -= 400;
        point2.y -= 300;
    });
}

function update ()
{
    graphics.clear();

    angle += 0.005;

    // vector starting at 0/0
    point.setTo(Math.cos(angle) * 250, Math.sin(angle) * 250);

    // drawn from the center (as if center was 0/0)
    graphics.lineBetween(400, 300, 400 + point.x, 300 + point.y);

    graphics.lineStyle(2, 0x00aa00);
    graphics.lineBetween(400, 300, 400 + point2.x, 300 + point2.y);

    var cross = point.cross(point2);

    var area = point.length() * point2.length();

    var angleBetween = Math.asin(cross / area);

    text.setText([
        'Cross product: ' + cross,
        'Normalized cross product: ' + cross / area,
        'Sinus of the angle between vectors: '+ Phaser.Math.RadToDeg(angleBetween),
        'Green vector is on the ' + (cross > 0 ? 'right' : 'left')
    ].join('\n'));
}

geom/point/equals.js:
var config = {
    width: 800,
    height: 600,
    type: Phaser.AUTO,
    parent: 'phaser-example',
    scene: {
        create: create
    }
};

var game = new Phaser.Game(config);

function create ()
{
    var graphics = this.add.graphics({ fillStyle: { color: 0x2266aa } });

    var points = [];

    for(var i = 0; i < 100; i++)
    {
        var x = Phaser.Math.Between(1, 40) * 20;
        var y = Phaser.Math.Between(1, 30) * 20;
        points.push(new Phaser.Geom.Point(x, y));
    }

    var pointerPoint = new Phaser.Geom.Point();

    this.input.on('pointermove', function (pointer) {

        // round position to 20 pixels
        var x = Math.round(pointer.x / 20) * 20;
        var y = Math.round(pointer.y / 20) * 20;

        pointerPoint.setTo(x, y);

        redraw();
    });

    redraw();

    function redraw ()
    {
        graphics.clear();

        var strokeRed = false;

        for(var i = 0; i < points.length; i++)
        {
            graphics.fillPointShape(points[i], 15);
            strokeRed = strokeRed || Phaser.Geom.Point.Equals(pointerPoint, points[i]);
        }

        if(strokeRed)
        {
            graphics.fillStyle(0xaa0000);
        }
        else
        {
            graphics.fillStyle(0x0000aa);
        }

        graphics.fillPointShape(pointerPoint, 15);
    }
}

