    rect = new Phaser.Geom.Rectangle(400, 300, 0, 0);
}

function update ()
{
    graphics.clear();

    rect.width += 2.4 * direction;
    rect.height += 1.8 * direction;

    if(rect.width * direction >= 200)
    {
