    var graphics = this.add.graphics();

    var color = 0xffff00;

    graphics.lineStyle(2, color);
    graphics.strokeRect(zone.x + zone.input.hitArea.x, zone.y + zone.input.hitArea.y, zone.input.hitArea.width, zone.input.hitArea.height);

