loader/base64/bitmap text.js:
var config = {
    type: Phaser.AUTO,
    parent: 'phaser-example',
    scene: {
        create: create
    }
};

var game = new Phaser.Game(config);

function create ()
{
    //  1) Decode the xml
    var xml = Phaser.DOM.ParseXML(atob(base64EncodedXML));

    //  2) Create an Image element from the encoded png:
    var image = new Image();

    var _this = this;

    image.onload = function ()
    {
        //  4) Successful decode? Then create the texture:
        var texture = _this.textures.addImage('desyrel', image);

        //  5) Create the Bitmap Font data
        var data = Phaser.GameObjects.BitmapText.ParseXMLBitmapFont(
            xml,
            _this.textures.getFrame('desyrel'),
            0, 0,
            texture
        );

        //  6) Add the bitmap font entry - the structure of the object is important
        _this.cache.bitmapFont.add('desyrel', { data: data, texture: 'desyrel', frame: null });

        //  Now we're free to create bitmap text objects, like this ...
        _this.add.bitmapText(0, 0, 'desyrel', 'Bitmap Text Decoded from base64');
    };

    //  3) Decode the image
    image.src = base64EncodedPNG;
}

var base64EncodedXML = "";
var base64EncodedPNG = "data:image/png;base64,";

loader/binary/load binary data from obje:
var config = {
    type: Phaser.AUTO,
    parent: 'phaser-example',
    width: 800,
    height: 600,
    backgroundColor: '#0072bc',
    scene: {
        preload: preload,
        create: create
    }
};

var game = new Phaser.Game(config);

function preload ()
{
    this.load.binary({
        key: 'mod',
        url: 'assets/audio/protracker/global_trash_3_v2.mod',
        dataType: Uint8Array
    });
}

function create ()
{
    var buffer = this.cache.binary.get('mod');

    //   getString scans the binary file between the two values given, 
    //   returning the characters it finds there as a string

    var signature = getString(buffer, 1080, 1084);

    var text = this.add.text(32, 32, "Signature: " + signature, { fill: '#ffffff' });
    text.setShadow(2, 2, 'rgba(0,0,0,0.5)', 0);

    var title = getString(buffer, 0, 20)
    var text2 = this.add.text(32, 64, "Title: " + title, { fill: '#ffffff' });
    text2.setShadow(2, 2, 'rgba(0,0,0,0.5)', 0);

    //  Get the sample data
    var sampleText = [];

    for (var i = 0; i < 31; i++)
    {
        var st = 20 + i * 30;
        sampleText.push(getString(buffer, st, st + 22));
    }

    var text3 = this.add.text(400, 32, sampleText, { fill: '#ffffff' });
    text3.setShadow(2, 2, 'rgba(0,0,0,0.5)', 0);
}

function getString (buffer, start, end)
{
    var output = '';

    for (var i = start; i < end; i++)
    {
        output += String.fromCharCode(buffer[i]);
    }

    return output;
}

