    {
        player.y -= 5;
    }
    else if (cursors.down.isDown)
    {
        player.y += 5;
    }

    var dist = Phaser.Math.Distance.Snake(player.x, player.y, ufo.x, ufo.y);

    graphic
        .clear()
        .strokePoints([
            { x: player.x + dist, y: player.y },
            { x: player.x, y: player.y + dist },
            { x: player.x - dist, y: player.y },
            { x: player.x , y: player.y - dist },
        ], true, true)
}

