        acceleration: 0.06,
        drag: 0.0005,
        maxSpeed: 1.0
    };

    controls = new Phaser.Cameras.Controls.SmoothedKeyControl(controlConfig);

    this.cameras.main.scrollX = 2770;
    this.cameras.main.scrollY = 2036;
    this.cameras.main.zoom = 0.12;
}

