function create ()
{

    // Friction movement is parallel to the movement of the immovable body
    // and perpendicular to the direction of overlap/separation

    var block = this.physics.add.image(400, 200, 'block')
        .setFriction(0, 1)
        .setImmovable(true)
        .setVelocityY(100);

