        }
        else
        {
            arrow.angle += chunk;
            spineBoy.angle += chunk;
            // arrow.rotation += chunk;
            // spineBoy.root.rotation += chunk;
        }

        text.setText([
            arrow.angle,
            arrow.rotation,
            spineBoy.angle,
            spineBoy.rotation,
            spineBoy.root.rotation
        ]);

    }, this);
}

