    }
};

var back;
var bob;
var graphics;
var offset;
var tween;
var iter = 0;

var game = new Phaser.Game(config);

function preload ()
{
    this.load.image('mushroom', 'assets/sprites/mushroom-32x32.png');
}

function create ()
{
    back = this.add.tileSprite(400, 300, 32*18, 32*12, 'mushroom').setAlpha(0.2);

    bob = this.add.tileSprite(400, 300, 32*18, 32*12, 'mushroom');

    graphics = this.add.graphics();

    var cropWidth = 200;
    var cropHeight = 100;

    bob.setCrop(0, 0, cropWidth, cropHeight);

    offset = bob.getTopLeft();

    this.input.on('pointermove', function (pointer) {

        bob.setCrop(
            (pointer.x - offset.x) - cropWidth / 2,
            (pointer.y - offset.y) - cropHeight / 2,
            cropWidth,
            cropHeight
        );
    });

    tween = this.tweens.addCounter({
        from: 1,
        to: 2,
        duration: 5000,
        ease: 'Sine.easeInOut',
        yoyo: true,
        repeat: -1
    });
}

function update ()
{
    graphics.clear();
    graphics.lineStyle(1, 0x00ff00);
    graphics.strokeRect(offset.x + bob._crop.x, offset.y + bob._crop.y, bob._crop.width, bob._crop.height);

    back.tilePositionX = Math.cos(iter) * 700;
    back.tilePositionY = Math.sin(iter) * 500;
    back.tileScaleX = tween.getValue();
    back.tileScaleY = tween.getValue();

    bob.tilePositionX = Math.cos(iter) * 700;
    bob.tilePositionY = Math.sin(iter) * 500;
    bob.tileScaleX = tween.getValue();
    bob.tileScaleY = tween.getValue();

    iter += 0.001;
}

textures/edit texture hue shift.js:
var config = {
    type: Phaser.AUTO,
    parent: 'phaser-example',
    width: 800,
    height: 600,
    scene: {
        preload: preload,
        create: create
    }
};

var originalTexture;
var newTexture;
var context; 

var game = new Phaser.Game(config);

function preload() 
{
    this.load.image('dude', 'assets/sprites/phaser-dude.png');
}

function create() 
{
    originalTexture = this.textures.get('dude').getSourceImage();

    newTexture = this.textures.createCanvas('dudeNew', originalTexture.width, originalTexture.height);

    context = newTexture.getSourceImage().getContext('2d');

    context.drawImage(originalTexture, 0, 0);

    this.add.image(100, 100, 'dude');
    this.add.image(200, 100, 'dudeNew');

    this.time.addEvent({ delay: 500, callback: hueShift , loop: true });
}

function hueShift ()
{
    var pixels = context.getImageData(0, 0, originalTexture.width, originalTexture.height);

    for (i = 0; i < pixels.data.length / 4; i++)
    {
        processPixel(pixels.data, i * 4, 0.1);
    }

    context.putImageData(pixels, 0, 0);

    newTexture.refresh();
}

function processPixel (data, index, deltahue)
{
    var r = data[index];
    var g = data[index + 1];
    var b = data[index + 2];

    var hsv = Phaser.Display.Color.RGBToHSV(r, g, b);

    var h = hsv.h + deltahue;

    var rgb = Phaser.Display.Color.HSVToRGB(h, hsv.s, hsv.v);

    data[index] = rgb.r;
    data[index + 1] = rgb.g;
    data[index + 2] = rgb.b;
}

