tilemap/endless map.js:
var config = {
    type: Phaser.AUTO,
    width: 800,
    height: 600,
    parent: 'phaser-example',
    scene: {
        preload: preload,
        create: create,
        update: update
    }
};

var map;
var text;
var sx = 0;
var mapWidth = 51;
var mapHeight = 37;
var distance = 0;
var tiles = [ 7, 7, 7, 6, 6, 6, 0, 0, 0, 1, 1, 2, 3, 4, 5 ];

var game = new Phaser.Game(config);

function preload ()
{
    this.load.image('tiles', 'assets/tilemaps/tiles/muddy-ground.png');
    this.load.bitmapFont('nokia16', 'assets/fonts/bitmap/nokia16.png', 'assets/fonts/bitmap/nokia16.xml');
}

function create ()
{
    var mapData = [];

    for (var y = 0; y < mapHeight; y++)
    {
        var row = [];

        for (var x = 0; x < mapWidth; x++)
        {
            //  Scatter the tiles so we get more mud and less stones
            var tileIndex = Phaser.Math.RND.weightedPick(tiles);

            row.push(tileIndex);
        }

        mapData.push(row);
    }

    map = this.make.tilemap({ data: mapData, tileWidth: 16, tileHeight: 16 });

    var tileset = map.addTilesetImage('tiles');
    var layer = map.createLayer(0, tileset, 0, 0);

    text = this.add.bitmapText(0, 0, 'nokia16').setScrollFactor(0);
}

function update (time, delta)
{
    //  Any speed as long as 16 evenly divides by it
    sx += 4;

    distance += sx;

    text.setText("Distance: " + distance + 'px');

    if (sx === 16)
    {
        //  Reset and create new strip

        var tile;
        var prev;

        for (var y = 0; y < mapHeight; y++)
        {
            for (var x = 1; x < mapWidth; x++)
            {
                tile = map.getTileAt(x, y);
                prev = map.getTileAt(x - 1, y);

                prev.index = tile.index;

                if (x === mapWidth - 1)
                {
                    tile.index = Phaser.Math.RND.weightedPick(tiles);
                }
            }
        }

        sx = 0;
    }

    this.cameras.main.scrollX = sx;
}

tilemap/fill tiles.js:
var config = {
    type: Phaser.WEBGL,
    width: 800,
    height: 600,
    backgroundColor: '#2d2d2d',
    parent: 'phaser-example',
    pixelArt: true,
    scene: {
        preload: preload,
        create: create,
        update: update
    }
};

var game = new Phaser.Game(config);
var controls;
var marker;
var map;

function preload ()
{
    this.load.image('tiles', 'assets/tilemaps/tiles/tmw_desert_spacing.png');
    this.load.tilemapTiledJSON('map', 'assets/tilemaps/maps/desert.json');
}

function create ()
{
    map = this.make.tilemap({ key: 'map' });
    var tiles = map.addTilesetImage('Desert', 'tiles');
    var layer = map.createLayer('Ground', tiles, 0, 0);

    marker = this.add.graphics();
    marker.lineStyle(2, 0x000000, 1);
    marker.strokeRect(0, 0, 6 * map.tileWidth, 6 * map.tileHeight);

    this.cameras.main.setBounds(0, 0, map.widthInPixels, map.heightInPixels);

    var cursors = this.input.keyboard.createCursorKeys();
    var controlConfig = {
        camera: this.cameras.main,
        left: cursors.left,
        right: cursors.right,
        up: cursors.up,
        down: cursors.down,
        speed: 0.5
    };
    controls = new Phaser.Cameras.Controls.FixedKeyControl(controlConfig);

    var help = this.add.text(16, 16, 'Left-click to fill the selected region.', {
        fontFamily: 'Arial',
        fontSize: '18px',
        padding: { x: 10, y: 5 },
        backgroundColor: '#000000',
        fill: '#ffffff'
    });
    help.setScrollFactor(0);
}

function update (time, delta)
{
    controls.update(delta);

    var worldPoint = this.input.activePointer.positionToCamera(this.cameras.main);

    // Rounds down to nearest tile
    var pointerTileX = map.worldToTileX(worldPoint.x);
    var pointerTileY = map.worldToTileY(worldPoint.y);

    // Snap to tile coordinates, but in world space
    marker.x = map.tileToWorldX(pointerTileX);
    marker.y = map.tileToWorldY(pointerTileY);

    if (this.input.manager.activePointer.isDown)
    {
        // Fill the tiles within an area with sign posts (tile id = 46)
        map.fill(46, pointerTileX, pointerTileY, 6, 6);
    }

}

tilemap/get chebyshev distance.js:
var config = {
    type: Phaser.AUTO,
    width: 800,
    height: 600,
    parent: 'phaser-example',
    pixelArt: true,
    physics: {
        default: 'arcade'
    },
    scene: {
        preload: preload,
        create: create,
        update: update
    }
};

var game = new Phaser.Game(config);
var map;
var cursors;
var debugGraphics;
var helpText;
var player;
var showDebug = false;

function preload ()
{
    this.load.image('tiles', 'assets/tilemaps/tiles/catastrophi_tiles_16.png');
    this.load.tilemapCSV('map', 'assets/tilemaps/csv/catastrophi_level2.csv');
    this.load.spritesheet('player', 'assets/sprites/spaceman.png', { frameWidth: 16, frameHeight: 16 });
}

function create ()
{
    // When loading a CSV map, make sure to specify the tileWidth and tileHeight
    map = this.make.tilemap({ key: 'map', tileWidth: 16, tileHeight: 16 });
    var tileset = map.addTilesetImage('tiles');
    var layer = map.createLayer(0, tileset, 0, 0);

    this.anims.create({
        key: 'left',
        frames: this.anims.generateFrameNumbers('player', { start: 8, end: 9 }),
        frameRate: 10,
        repeat: -1
    });
    this.anims.create({
        key: 'right',
        frames: this.anims.generateFrameNumbers('player', { start: 1, end: 2 }),
        frameRate: 10,
        repeat: -1
    });
    this.anims.create({
        key: 'up',
        frames: this.anims.generateFrameNumbers('player', { start: 11, end: 13 }),
        frameRate: 10,
        repeat: -1
    });
    this.anims.create({
        key: 'down',
        frames: this.anims.generateFrameNumbers('player', { start: 4, end: 6 }),
        frameRate: 10,
        repeat: -1
    });

    player = this.physics.add.sprite(400, 300, 'player', 1);

    this.cameras.main.setBounds(0, 0, map.widthInPixels, map.heightInPixels);
    this.cameras.main.startFollow(player);

    cursors = this.input.keyboard.createCursorKeys();
}

function update (time, delta)
{
    updatePlayer();
    updateMap();
}

function updateMap () {
    var origin = map.getTileAtWorldXY(player.x, player.y);

    map.forEachTile(function (tile) {
        var dist = Phaser.Math.Distance.Chebyshev(
            origin.x,
            origin.y,
            tile.x,
            tile.y
        );

        tile.setAlpha(1 - 0.1 * dist);
    });
}

function updatePlayer () {
    player.body.setVelocity(0);

    // 8 directions

    if (cursors.left.isDown) {
        player.body.setVelocityX(-100);
    }
    else if (cursors.right.isDown) {
        player.body.setVelocityX(100);
    }

    if (cursors.up.isDown) {
        player.body.setVelocityY(-100);
    }
    else if (cursors.down.isDown) {
        player.body.setVelocityY(100);
    }

    // Update the animation last and give left/right animations precedence over up/down animations
    if (cursors.left.isDown) {
        player.anims.play('left', true);
    }
    else if (cursors.right.isDown) {
        player.anims.play('right', true);
    }
    else if (cursors.up.isDown) {
        player.anims.play('up', true);
    }
    else if (cursors.down.isDown) {
        player.anims.play('down', true);
    }
    else {
        player.anims.stop();
    }
}

tilemap/get tiles in shape.js:
var config = {
    type: Phaser.AUTO,
    width: 800,
    height: 600,
    backgroundColor: '#1affdd',
    parent: 'phaser-example',
    pixelArt: true,
    scene: {
        preload: preload,
        create: create,
        update: update
    }
};

var game = new Phaser.Game(config);
var p1 = null;
var p2 = null;
var map;
var controls;
var graphics;
var selectedShape = 'rectangle';
var onlyColliding = false;

function preload() {
    this.load.tilemapTiledJSON('map', 'assets/tilemaps/maps/cybernoid.json');
    this.load.image('cybernoid', 'assets/tilemaps/tiles/cybernoid.png');
}

function create() {
    map = this.add.tilemap('map');
    var tiles = map.addTilesetImage('cybernoid');
    var layer = map.createLayer(0, tiles);
    // var layer = map.createDynamicLayer(0, tiles);

    layer.setScale(1.25, 1.25);

    graphics = this.add.graphics({
        lineStyle: { width: 4, color: 0xa8fff2 },
        fillStyle: { color: 0xa8fff2 }
    });

    map.setCollisionByExclusion(7);

    this.input.keyboard.on('keydown-ONE', function (event) {
        selectedShape = 'rectangle';
        helpText.setText(getHelpMessage());
    });

    this.input.keyboard.on('keydown-TWO', function (event) {
        selectedShape = 'line';
        helpText.setText(getHelpMessage());
    });

    this.input.keyboard.on('keydown-THREE', function (event) {
        selectedShape = 'circle';
        helpText.setText(getHelpMessage());
    });

    this.input.keyboard.on('keydown-FOUR', function (event) {
        selectedShape = 'triangle';
        helpText.setText(getHelpMessage());
    });

    this.input.keyboard.on('keydown-C', function (event) {
        onlyColliding = !onlyColliding;
        helpText.setText(getHelpMessage());
    });

    this.cameras.main.setBounds(0, 0, map.widthInPixels, map.heightInPixels);

    var cursors = this.input.keyboard.createCursorKeys();
    var controlConfig = {
        camera: this.cameras.main,
        left: cursors.left,
        right: cursors.right,
        up: cursors.up,
        down: cursors.down,
        speed: 0.5
    };
    controls = new Phaser.Cameras.Controls.FixedKeyControl(controlConfig);

    var helpText = this.add.text(16, 16, getHelpMessage(), {
        fontSize: '18px',
        padding: { x: 10, y: 5 },
        fill: '#ffffff',
        backgroundColor: '#000000'
    });
    helpText.setScrollFactor(0);

    this.input.on('pointerdown', function () {

        // Update p1 & p2 based on where user clicks
        var worldPoint = this.input.activePointer.positionToCamera(this.cameras.main);
        if (!p1) {
            p1 = worldPoint.clone();
        }
        else if (!p2) {
            p2 = worldPoint.clone();
        }
        else {
            p1 = worldPoint.clone();
            p2 = null;
        }

    }, this);
}

function update(time, delta) {
    controls.update(delta);
    graphics.clear();

    // Show user where they clicked
    if (p1) { graphics.fillCircle(p1.x, p1.y, 3); }
    if (p2) { graphics.fillCircle(p2.x, p2.y, 3); }

    // If we have both points, draw a shape and manipulate the tiles in that shape
    if (p1 && p2) {
        map.forEachTile(function (tile) { tile.alpha = 1; });

        var overlappingTiles = [];

        switch (selectedShape) {
            case 'rectangle':
                var xStart = Math.min(p1.x, p2.x);
                var yStart = Math.min(p1.y, p2.y);
                var xEnd = Math.max(p1.x, p2.x);
                var yEnd = Math.max(p1.y, p2.y);
                var rect = new Phaser.Geom.Rectangle(xStart, yStart, xEnd - xStart, yEnd - yStart);
                overlappingTiles = map.getTilesWithinShape(rect, { isColliding: onlyColliding });
                graphics.strokeRectShape(rect);
                break;
            case 'line':
                var line = new Phaser.Geom.Line(p1.x, p1.y, p2.x, p2.y);
                overlappingTiles = map.getTilesWithinShape(line, { isColliding: onlyColliding });
                graphics.strokeLineShape(line);
                break;
            case 'circle':
                var radius = Math.sqrt(Math.pow(p2.x - p1.x, 2) + Math.pow(p2.y - p1.y, 2)) / 2;
                var cx = (p1.x + p2.x) / 2;
                var cy = (p1.y + p2.y) / 2;
                var circle = new Phaser.Geom.Circle(cx, cy, radius);
                overlappingTiles = map.getTilesWithinShape(circle, { isColliding: onlyColliding });
                graphics.strokeCircleShape(circle);
                break;
            case 'triangle':
                var tri = new Phaser.Geom.Triangle(p1.x, p1.y, p1.x, p2.y, p2.x, p2.y);
                overlappingTiles = map.getTilesWithinShape(tri, { isColliding: onlyColliding });
                graphics.strokeTriangleShape(tri);
                break;
            default:
                break;
        }

        overlappingTiles.forEach(function (tile) { tile.alpha = 0.25; });
    }

}

function getHelpMessage() {
    return 'Click to draw. Press 1/2/3/4 to change shapes.' +
        '\nSelected shape: ' + selectedShape +
        '\nPress C to only select colliding tiles: ' + (onlyColliding ? 'on' : 'off') +
        '\nArrows to scroll.';
}

