var totalTests = 0;
var testsPassed = 0;
var assert = (message, condition) => {
    totalTests++;
    if (condition) testsPassed++;
    console.assert(condition, message)
};

var game = new Phaser.Game(config);

function preload() {
    this.load.tilemapTiledJSON('desert', 'assets/tilemaps/maps/desert.json');
