function update() {

    if (d === 0)
    {
        atlasFrame.scale += 0.01;
        singleImage.scale += 0.01;

        if (singleImage.scale >= 4)
        {
            d = 1;
        }
    }
    else
    {
        atlasFrame.scale -= 0.01;
        singleImage.scale -= 0.01;

        if (singleImage.scale <= 0)
        {
            d = 0;
        }
    }

}

