let tween;
const easeTypes = [
    'Back',
    'Bounce',
    'Circular',
    'Cubic',
    'Elastic',
    'Expo',
    'Expo.easeInOut',
    'Linear',
    'Quadratic',
    'Quartic',
    'Quintic',
    'Sine',
    'Sine.easeInOut',
    'Sine.easeOut',
    'Stepped'
];
const easeTypeParams = [
    [],
    [],
    [],
    [],
    [],
    [],
    [],
    [],
    [],
    [],
    [],
    [],
    [],
    [],
    [5],
];

function preload()
